mapper.select:
select contract_name,money
from contract
where from_contract_code = #{id,jdbcType=VARCHAR}

select product_code as productCode,name,customer_name as customerName,status
from project  where 1=1
 AND  product_code =#{projectCode,jdbcType=VARCHAR}

select a.id,a.contract_code, a.project_code,a.project_name, a.contract_type, a.contract_name, a.from_contract_code, a.from_contract_name, a.customer_resource,a.money, a.creater_name, a.creater_account, a.creater_time, a.update_time, a.status,a.attachment_detail from contract a where 1=1
AND a.creater_time >= #{queryBean.startTime,jdbcType=VARCHAR}
AND a.creater_time <= #{queryBean.endTime,jdbcType=VARCHAR}
AND a.contract_type= #{queryBean.contractType,jdbcType=INTEGER}
AND a.status= #{queryBean.contractStatus,jdbcType=INTEGER}
AND ( a.project_name like CONCAT(CONCAT('%', #{queryBean.queryName,jdbcType=VARCHAR}),'%') or a.contract_name like CONCAT(CONCAT('%', #{queryBean.queryName,jdbcType=VARCHAR}),'%') or a.creater_name like CONCAT(CONCAT('%', #{queryBean.queryName,jdbcType=VARCHAR}),'%') )
AND a.contract_code= #{queryBean.contractCode,jdbcType=INTEGER}
AND a.status in ('1','2','3','4','5','6','7','8','9')
AND a.status in ('2','3','4','5','6','7','8','9')
AND a.status in ('3','4','5','6','7','8','9')
AND a.status in ('4','5','6','7','8','9')
AND a.creater_account in (${queryBean.userList})
order by a.creater_time desc

mapper.update:
update contract
set status = #{status,jdbcType=INTEGER},update_time=#{date,jdbcType=VARCHAR}
where id = #{id,jdbcType=VARCHAR}

update contract
set attachment_detail = #{url,jdbcType=INTEGER}
where id = #{id,jdbcType=VARCHAR}

update project
set contract_code = #{contractCode,jdbcType=INTEGER}
where product_code = #{projectCode,jdbcType=VARCHAR}

update attachment
set is_extra = 0
where correlation_id = #{id,jdbcType=VARCHAR}

mapper.select:
	select 

     from contract
     where project_code = #{projectCode,jdbcType=VARCHAR} AND contract_type = 1 and status = ${status}
