mapper.select:
        select 
        id, project_name projectName, project_code projectCode, model_name modelName, status status, create_time createTime, 
    creator_name creatorName 
        from pending_event
        where is_show = 1

				and   create_time >= #{startTime} and create_time <= #{endTime}  	


				and ( project_name like concat('%',#{key},'%')  or model_name like concat('%',#{key},'%')) 


				and role_type = #{roleType}


				and creator_account = #{account}


            order by create_time desc

    select 

    from pending_event
    where is_show =1 

    	and project_code = #{projectCode,jdbcType=VARCHAR}


    	and model_code = #{modelCode,jdbcType=VARCHAR}


    	and model_name = #{modelName,jdbcType=VARCHAR}


		and role_type = #{roleType}

    and exception_id is null

select
from pending_event where is_show =1
and exception_id = #{exceptionId,jdbcType=VARCHAR}

select 

from pending_event
where is_show =1 

	and project_code = #{projectCode,jdbcType=VARCHAR}


	and role_type in concat('(',#{roleTypes},')')


	and model_name = #{modelName,jdbcType=VARCHAR}


	and model_code = #{modelCode,jdbcType=VARCHAR}

and exception_id is null

select 

from pending_event
where is_show =1 

	and project_code = #{projectCode,jdbcType=VARCHAR}


	and model_code = #{modelCode,jdbcType=VARCHAR}


	and model_name = #{modelName,jdbcType=VARCHAR}


	and role_type = #{roleType,jdbcType=VARCHAR}


	and exception_id = #{exceptionId,jdbcType=VARCHAR}

mapper.delete:
    delete from pending_event
    where 1=1

	    	and project_code = #{projectCode,jdbcType=VARCHAR}


	    	and model_code = #{modelCode,jdbcType=VARCHAR}


	    	and model_name = #{modelName,jdbcType=VARCHAR}


	    	and role_type = #{roleType,jdbcType=VARCHAR}


	    	and exception_id = #{exceptionId,jdbcType=VARCHAR}
